import React from 'react';
import Menu from '../Assets/menu.png';

class Header extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        visibility: false
    };
    this.toggleNavBar = this.toggleNavBar.bind(this);
  }

  toggleNavBar(){
      this.setState(state => ({
         visibility: !state.visibility 
      }))
  }

  render () {
    let navBar = (
      <div className="d-flex flex-column flex-sm-row align-items-center bg-light">
        <a className="m-2" href="./About" style={{color: '#03045E', textDecoration:'none'}}>About</a>
        <a className="m-2" href="./Stack" style={{color: '#03045E', textDecoration:'none'}}>Stack</a>
        <a className="m-2" href="./Projects" style={{color: '#03045E', textDecoration:'none'}}>Works</a>
        <a className="m-2" href="./Testimonials" style={{color: '#03045E', textDecoration:'none'}}>Testimonials</a>
        <button type="button" className="btn" style={{color: '#03045E', textDecoration:'none', border:'2px solid #03045E', width: 'fit-content'}}>Hire Me</button>
      </div>
          );
    let none = ("");
    return (
    <div className="w-100 border-bottom border-dark sticky-top d-flex flex-column flex-sm-row align-items-between justify-content-sm-center" style={{color: '#03045E', backgroundColor: '#CAF0F8'}}>
      <div className="d-flex flex-row ">
        <h1 className="logo col-sm-auto">Logo</h1>
        <button className="bg-transparent border-0 d-sm-none" style={{width: 'fit-content'}} onClick={this.toggleNavBar}><img src={Menu}alt="menu" style={{width:25, height: 'auto'}}/></button>
        <span className="d-none d-sm-block">{navBar}</span>
      </div>
      <span className="d-sm-none">{this.state.visibility? navBar: none}</span>
    </div>
    );
  }
}

export { Header }
