import React from 'react';
import { useForm } from 'react-hook-form';
import TwitterLogo from '../Assets/twitter.svg';
import LinkedInLogo from '../Assets/linkedin.svg';
import InstagramLogo from '../Assets/instagram.svg';
import '../App.css';

function Contact(props) {
    const {register, handleSubmit, watch, errors } = useForm();
    const onSubmit = (data) => {
        console.log(data);
    }
    return (
      <div id="contact" className="surface1-dark w-100 p-5 align-items-center" style={{height: 'fit-content', color: '#00ADB5'}}>
         <h1>Contact</h1>
         <p className="text2-dark" style={{color:'#EEEEEE'}}>Let's rub minds and work together ðŸ˜€</p>
         <form class="container d-flex flex-column text-start rounded-4 p-3 w-sm-70" style={{border:'2px solid #00ADB5', maxWidth: 500}} id="contact-form" onSubmit={handleSubmit(onSubmit)}>
            <label for="name" className="d-flex flex-column my-2">Name
            <input id="name" required name="user_name" type="text" placeHolder="Enter your name" className="surface2-dark border-0 rounded-3" style={{height:50, color:'#EEEEEE'}} ref={register}/>
            </label>
            <label for="email" className="d-flex flex-column my-2">Email
            <input required name="user_email" id="email" type="email" placeHolder="Enter your email" className="surface2-dark border-0 rounded-3" style={{height:50, color:'#EEEEEE'}} ref={register}/>
            </label>
            <label for="message" className="d-flex flex-column my-2">Got some info for me?
            <textarea name="message" id="message" type="text" placeHolder="Message" className="surface2-dark border-0 rounded-3" style={{height: 100, color:'#EEEEEE'}} ref={register}/>
            </label>
            <button type="submit" className="btn block my-2 btn-hover text1-dark brand-light" style={{}}>Message Me</button>
         </form>
         <div className="w-100 my-3 mx-auto d-flex flex-row justify-content-center">
            <a href="https://instagram.com/codeevolver"><img src={InstagramLogo} alt="Instagram link" style={{width:'3rem', margin: 30}}/></a>
            <a href="https://www.linkedin.com/in/goodness-o-939796225"><img src={LinkedInLogo} alt="LinkedIn link"style={{width:'3rem', margin: 30}} /></a>
            <a href="https://twitter.com/code_evolver"><img src={TwitterLogo} alt="Twitter link" style={{width:'3rem', margin: 30}} /></a>
         </div>
      </div>
    );
}

export { Contact }
